import React, { useState, useEffect } from 'react';
import '../../styles/ManageReceipts.css';

const ManageReceipts = () => {
  const [receipts, setReceipts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [activeTab, setActiveTab] = useState('list');
  const [formData, setFormData] = useState({
    studentId: '',
    selectedPayment: null
  });
  const [studentDetails, setStudentDetails] = useState(null);
  const [studentPayments, setStudentPayments] = useState([]);
  const [fetchingData, setFetchingData] = useState(false);
  const [studentNames, setStudentNames] = useState({});
  const [filters, setFilters] = useState({
    course: '',
    year: '',
    semester: '',
    status: ''
  });

  useEffect(() => {
    fetchReceipts();
  }, []);

  const fetchReceipts = async () => {
    try {
      const token = localStorage.getItem('token');
      const queryParams = new URLSearchParams();

      if (filters.course) queryParams.append('course', filters.course);
      if (filters.year) queryParams.append('year', filters.year);
      if (filters.semester) queryParams.append('semester', filters.semester);
      if (filters.status) queryParams.append('status', filters.status);
      // Note: sessionId filter removed to avoid conflicts with duplicate receipts

      const url = `http://localhost:5000/api/receipts${queryParams.toString() ? '?' + queryParams.toString() : ''}`;
      const response = await fetch(url, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        setReceipts(data);

        // Collect studentIds that need name fetching
        const studentIdsToFetch = [];
        const names = {};

        for (const receipt of data) {
          if (receipt.studentName && receipt.studentName !== 'undefined undefined' && receipt.studentName.trim() !== '') {
            names[receipt.studentId] = receipt.studentName;
          } else {
            if (!studentIdsToFetch.includes(receipt.studentId)) {
              studentIdsToFetch.push(receipt.studentId);
            }
          }
        }

        // Fetch names for missing ones
        for (const studentId of studentIdsToFetch) {
          try {
            const studentResponse = await fetch(`http://localhost:5000/api/payments/student-details/${studentId}`, {
              headers: {
                'Authorization': `Bearer ${token}`
              }
            });
            if (studentResponse.ok) {
              const studentData = await studentResponse.json();
              names[studentId] = `${studentData.firstName || ''} ${studentData.middleName || ''} ${studentData.lastName || ''}`.trim();
            }
          } catch (error) {
            console.error(`Error fetching name for student ${studentId}:`, error);
          }
        }
        setStudentNames(names);
      }
    } catch (error) {
      console.error('Error fetching receipts:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // If studentId changed, fetch student details and payments
    if (name === 'studentId' && value.trim()) {
      fetchStudentData(value.trim());
    }
  };

  const fetchStudentData = async (studentId) => {
    // Only fetch if studentId is complete (assuming format STUXXXXXX)
    if (studentId.length < 9) {
      setStudentDetails(null);
      setStudentPayments([]);
      setFormData(prev => ({ ...prev, selectedPayment: null }));
      return;
    }

    setFetchingData(true);
    setStudentDetails(null);
    setStudentPayments([]);
    setFormData(prev => ({ ...prev, selectedPayment: null }));

    try {
      const token = localStorage.getItem('token');

      // Fetch student details
      const studentResponse = await fetch(`http://localhost:5000/api/payments/student-details/${studentId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (studentResponse.ok) {
        const studentData = await studentResponse.json();
        setStudentDetails(studentData);
      }

      // Fetch student payments
      const paymentsResponse = await fetch(`http://localhost:5000/api/payments/student/${studentId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (paymentsResponse.ok) {
        const paymentsData = await paymentsResponse.json();
        // Filter only paid payments
        const paidPayments = paymentsData.filter(payment => payment.status === 'Paid');
        setStudentPayments(paidPayments);
      }
    } catch (error) {
      console.error('Error fetching student data:', error);
      setMessage('Error fetching student data');
    } finally {
      setFetchingData(false);
    }
  };

  const handlePaymentSelect = (payment) => {
    setFormData(prev => ({
      ...prev,
      selectedPayment: payment,
      paymentId: payment.paymentId
    }));
  };

  const handleGenerateReceipt = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    if (!formData.selectedPayment) {
      setMessage('Please select a payment to generate receipt');
      setLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/receipts/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ paymentId: formData.paymentId })
      });

      if (response.ok) {
        setMessage('Receipt generated successfully!');
        fetchReceipts();
        setFormData({
          studentId: '',
          selectedPayment: null
        });
        setStudentDetails(null);
        setStudentPayments([]);
        setActiveTab('list');
      } else {
        const error = await response.json();
        setMessage(error.message || 'Failed to generate receipt');
      }
    } catch (error) {
      setMessage('Error generating receipt: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadReceipt = async (receiptId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/receipts/${receiptId}/download`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `receipt_${receiptId}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      } else {
        setMessage('Failed to download receipt');
      }
    } catch (error) {
      setMessage('Error downloading receipt: ' + error.message);
    }
  };

  const handleCancelReceipt = async (receiptId) => {
    if (!window.confirm('Are you sure you want to cancel this receipt?')) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/receipts/${receiptId}/cancel`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        setMessage('Receipt cancelled successfully!');
        fetchReceipts();
      } else {
        setMessage('Failed to cancel receipt');
      }
    } catch (error) {
      setMessage('Error cancelling receipt: ' + error.message);
    }
  };

  const handleDuplicateReceipt = async (receiptId) => {
    if (!window.confirm('Are you sure you want to duplicate this receipt?')) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/receipts/${receiptId}/duplicate`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        setMessage('Duplicate receipt generated successfully!');
        fetchReceipts();
      } else {
        const error = await response.json();
        setMessage(error.message || 'Failed to duplicate receipt');
      }
    } catch (error) {
      setMessage('Error duplicating receipt: ' + error.message);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleApplyFilters = () => {
    fetchReceipts();
  };



  return (
    <div className="manage-receipts-container">
      <div className="manage-receipts-header">
        <h1>Manage Receipts</h1>
        <p>Generate, view, and manage payment receipts.</p>
      </div>

      {message && (
        <div className={`message ${message.includes('Error') || message.includes('Failed') ? 'error' : ''}`}>
          {message}
        </div>
      )}

      <div className="tab-buttons">
        <button
          onClick={() => setActiveTab('list')}
          className={`tab-button ${activeTab === 'list' ? 'active' : ''}`}
        >
          View Receipts
        </button>
        <button
          onClick={() => setActiveTab('generate')}
          className={`tab-button ${activeTab === 'generate' ? 'active' : ''}`}
        >
          Generate Receipt
        </button>
      </div>

      {activeTab === 'generate' && (
        <div className="generate-section">
          <h3>Generate New Receipt</h3>
          <form onSubmit={handleGenerateReceipt}>
            <div className="form-group">
              <label>Student ID:</label>
              <input
                type="text"
                name="studentId"
                value={formData.studentId}
                onChange={handleInputChange}
                required
                placeholder="Enter student ID to fetch details"
              />
            </div>

            {fetchingData && (
              <div className="loading-text">
                Fetching student data...
              </div>
            )}

            {studentDetails && (
              <div className="student-details">
                <h4>Student Details:</h4>
                <p><strong>Name:</strong> {studentDetails.firstName} {studentDetails.middleName || ''} {studentDetails.lastName}</p>
                <p><strong>Course:</strong> {studentDetails.course}</p>
                <p><strong>Year:</strong> {studentDetails.year}</p>
              </div>
            )}

            {studentPayments.length > 0 && (
              <div className="payment-list">
                <label>Select Payment:</label>
                <div className="payment-scroll">
                  {studentPayments.map(payment => (
                    <div
                      key={payment._id}
                      onClick={() => handlePaymentSelect(payment)}
                      className={`payment-item ${formData.selectedPayment && formData.selectedPayment._id === payment._id ? 'selected' : ''}`}
                    >
                      <div><strong>Payment ID:</strong> {payment.paymentId}</div>
                      <div><strong>Amount:</strong> â‚¹{payment.amount}</div>
                      <div><strong>Date:</strong> {new Date(payment.paymentDate).toLocaleDateString()}</div>
                      <div><strong>Method:</strong> {payment.paymentMethod}</div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {studentPayments.length === 0 && formData.studentId && !fetchingData && (
              <div className="no-payments">
                No paid payments found for this student.
              </div>
            )}

            <button
              type="submit"
              disabled={loading || !formData.selectedPayment}
              className="submit-button"
            >
              {loading ? 'Generating...' : 'Generate Receipt'}
            </button>
          </form>
        </div>
      )}

      {activeTab === 'list' && (
        <div className="receipts-list">
          <h3>Receipts List</h3>

          <div className="filters-section">
            <h3>Filters</h3>
            <div className="filters-grid">
              <div className="filter-group">
                <label>Course:</label>
                <select
                  name="course"
                  value={filters.course}
                  onChange={handleFilterChange}
                >
                  <option value="">All Courses</option>
                  <option value="B.A">B.A</option>
                  <option value="B.Sc">B.Sc</option>
                  <option value="B.Ed">B.Ed</option>
                </select>
              </div>
              <div className="filter-group">
                <label>Year:</label>
                <input
                  type="number"
                  name="year"
                  value={filters.year}
                  onChange={handleFilterChange}
                  placeholder="e.g., 2024"
                />
              </div>
              <div className="filter-group">
                <label>Semester:</label>
                <input
                  type="number"
                  name="semester"
                  value={filters.semester}
                  onChange={handleFilterChange}
                  placeholder="e.g., 1"
                />
              </div>
              <div className="filter-group">
                <label>Status:</label>
                <select
                  name="status"
                  value={filters.status}
                  onChange={handleFilterChange}
                >
                  <option value="">All Status</option>
                  <option value="active">Active</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>
              <div className="filter-group">
                <button onClick={handleApplyFilters} className="apply-filters-button">
                  Apply Filters
                </button>
              </div>
            </div>
          </div>

          <div style={{ maxHeight: '600px', overflowY: 'auto' }}>
            {receipts.length > 0 ? (
              <table className="receipts-table">
                <thead>
                  <tr>
                    <th>Receipt No</th>
                    <th>Student ID</th>
                    <th>Student Name</th>
                    <th>Amount</th>
                    <th>Status</th>
                    <th>Date</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {receipts.map(receipt => (
                    <tr key={receipt._id}>
                      <td>
                        {receipt.receiptNumber}
                      </td>
                      <td>
                        {receipt.studentId}
                      </td>
                      <td>
                        {studentNames[receipt.studentId] || 'Loading...'}
                      </td>
                      <td>
                        â‚¹{receipt.amount}
                      </td>
                      <td>
                        <span className={`status-badge ${receipt.status === 'Active' ? 'status-active' : 'status-cancelled'}`}>
                          {receipt.status}
                        </span>
                      </td>
                      <td>
                        {new Date(receipt.createdAt).toLocaleDateString()}
                      </td>
                      <td>
                        <button
                          onClick={() => handleDownloadReceipt(receipt._id)}
                          className="action-button download-button"
                        >
                          Download
                        </button>
                        {receipt.status === 'Active' && (
                          <>
                            <button
                              onClick={() => handleDuplicateReceipt(receipt._id)}
                              className="action-button duplicate-button"
                            >
                              Duplicate
                            </button>
                            <button
                              onClick={() => handleCancelReceipt(receipt._id)}
                              className="action-button cancel-button"
                            >
                              Cancel
                            </button>
                          </>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p className="no-receipts">No receipts found.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageReceipts;
