import React, { useState, useEffect } from 'react';

const DownloadReceipts = () => {
  const [receipts, setReceipts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [activeTab, setActiveTab] = useState('list');
  const [filters, setFilters] = useState({
    startDate: '',
    endDate: '',
    status: ''
  });

  useEffect(() => {
    fetchMyReceipts();
  }, []);

  const fetchMyReceipts = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/receipts/my', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        setReceipts(data);
      } else {
        setMessage('Failed to fetch receipts');
      }
    } catch (error) {
      console.error('Error fetching receipts:', error);
      setMessage('Error fetching receipts: ' + error.message);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleDownloadReceipt = async (receiptId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/receipts/${receiptId}/download`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `receipt_${receiptId}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      } else {
        setMessage('Failed to download receipt');
      }
    } catch (error) {
      setMessage('Error downloading receipt: ' + error.message);
    }
  };

  const filteredReceipts = receipts.filter(receipt => {
    const matchesStartDate = !filters.startDate || new Date(receipt.createdAt) >= new Date(filters.startDate);
    const matchesEndDate = !filters.endDate || new Date(receipt.createdAt) <= new Date(filters.endDate);
    const matchesStatus = !filters.status || receipt.status === filters.status;
    return matchesStartDate && matchesEndDate && matchesStatus;
  });

  return (
    <div className="menu-content">
      <h1>Download Receipts</h1>
      <p>View and download your payment receipts.</p>

      {message && (
        <div style={{
          padding: '10px',
          margin: '10px 0',
          border: '1px solid #ddd',
          borderRadius: '4px',
          backgroundColor: message.includes('Error') || message.includes('Failed') ? '#ffebee' : '#e8f5e8',
          color: message.includes('Error') || message.includes('Failed') ? '#c62828' : '#2e7d32'
        }}>
          {message}
        </div>
      )}

      <div style={{ marginBottom: '20px' }}>
        <button
          onClick={() => setActiveTab('list')}
          style={{
            padding: '10px 20px',
            marginRight: '10px',
            backgroundColor: activeTab === 'list' ? '#007bff' : '#6c757d',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          View Receipts
        </button>
      </div>

      {activeTab === 'list' && (
        <div>
          <h3>Receipts List</h3>

          {/* Filters */}
          <div style={{ marginBottom: '20px', display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
            <div>
              <label>Start Date:</label>
              <input
                type="date"
                name="startDate"
                value={filters.startDate}
                onChange={handleFilterChange}
                style={{ marginLeft: '5px', padding: '5px' }}
              />
            </div>
            <div>
              <label>End Date:</label>
              <input
                type="date"
                name="endDate"
                value={filters.endDate}
                onChange={handleFilterChange}
                style={{ marginLeft: '5px', padding: '5px' }}
              />
            </div>
            <div>
              <label>Status:</label>
              <select
                name="status"
                value={filters.status}
                onChange={handleFilterChange}
                style={{ marginLeft: '5px', padding: '5px' }}
              >
                <option value="">All Status</option>
                <option value="Active">Active</option>
                <option value="Cancelled">Cancelled</option>
              </select>
            </div>
          </div>

          {/* Receipt List */}
          <div style={{ maxHeight: '600px', overflowY: 'auto' }}>
            {filteredReceipts.length > 0 ? (
              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr style={{ backgroundColor: '#f8f9fa' }}>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Receipt No</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Amount</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Fee Breakdown</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Status</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Date</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReceipts.map(receipt => (
                    <tr key={receipt._id}>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {receipt.receiptNumber}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        â‚¹{receipt.amount}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {receipt.feeBreakdown || 'N/A'}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        <span style={{
                          padding: '3px 8px',
                          borderRadius: '12px',
                          backgroundColor: receipt.status === 'Active' ? '#28a745' : '#dc3545',
                          color: 'white',
                          fontSize: '12px'
                        }}>
                          {receipt.status}
                        </span>
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {new Date(receipt.createdAt).toLocaleDateString()}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {receipt.status === 'Active' && (
                          <button
                            onClick={() => handleDownloadReceipt(receipt._id)}
                            style={{
                              padding: '5px 10px',
                              backgroundColor: '#007bff',
                              color: 'white',
                              border: 'none',
                              borderRadius: '4px',
                              cursor: 'pointer'
                            }}
                          >
                            Download
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>No receipts found.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default DownloadReceipts;
