import React, { useState } from 'react';

const ViewDocuments = () => {
  // Mock data for demonstration. In a real app, this would come from the server.
  const [documents] = useState([
    { type: '10th (High School) Marksheet', status: 'Uploaded', fileName: '10th_marksheet.pdf' },
    { type: '10th (High School) Certificate', status: 'Uploaded', fileName: '10th_certificate.pdf' },
    { type: '12th (Intermediate) Marksheet', status: 'Pending', fileName: null },
    { type: '12th (Intermediate) Certificate', status: 'Uploaded', fileName: '12th_certificate.pdf' },
    { type: 'Transfer Certificate (TC) from last institute', status: 'Uploaded', fileName: 'tc.pdf' },
    { type: 'Character Certificate', status: 'Pending', fileName: null },
    { type: 'Caste Certificate', status: 'Uploaded', fileName: 'caste_certificate.pdf' },
    { type: 'Domicile Certificate', status: 'Pending', fileName: null },
    { type: 'Recent passport size photographs', status: 'Uploaded', fileName: 'passport_photo.jpg' },
    { type: 'Aadhaar Card or any valid Identity Proof', status: 'Uploaded', fileName: 'aadhaar.pdf' }
  ]);

  const handleDownload = (fileName) => {
    // In a real app, this would trigger a download from the server
    alert(`Downloading ${fileName}`);
  };

  return (
    <div className="view-documents">
      <h2>View Documents</h2>
      <table>
        <thead>
          <tr>
            <th>Document Type</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {documents.map((doc, index) => (
            <tr key={index}>
              <td>{doc.type}</td>
              <td>{doc.status}</td>
              <td>
                {doc.status === 'Uploaded' ? (
                  <button onClick={() => handleDownload(doc.fileName)}>Download</button>
                ) : (
                  <span>Not Uploaded</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewDocuments;
