import React, { useState, useEffect } from 'react';

const ViewFees = () => {
  const [feeStructures, setFeeStructures] = useState([]);
  const [payments, setPayments] = useState([]);
  const [studentInfo, setStudentInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [activeTab, setActiveTab] = useState('fees');
  const [paymentType, setPaymentType] = useState('semester');

  useEffect(() => {
    fetchStudentInfo();
    fetchFeeStructures();
    fetchPayments();
  }, []);

  const fetchStudentInfo = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/auth/me', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        setStudentInfo(data);
      }
    } catch (error) {
      console.error('Error fetching student info:', error);
    }
  };

  const fetchFeeStructures = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/fees', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        setFeeStructures(data);
      }
    } catch (error) {
      console.error('Error fetching fee structures:', error);
    }
  };

  const fetchPayments = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/payments/my-payments', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        setPayments(data);
      }
    } catch (error) {
      console.error('Error fetching payments:', error);
    }
  };

  const getStudentFeeStructure = () => {
    if (!studentInfo || !feeStructures.length) return null;
    return feeStructures.find(structure => structure.course === studentInfo.department);
  };

  const calculateFeeAmount = (structure, type) => {
    if (!structure) return 0;
    const total = structure.totalFee;
    return type === 'semester' ? total / 2 : total;
  };

  const initiatePayment = async () => {
    if (!studentInfo || !getStudentFeeStructure()) {
      setMessage('Unable to process payment. Student information or fee structure not found.');
      return;
    }

    const structure = getStudentFeeStructure();
    const amount = calculateFeeAmount(structure, paymentType);

    setLoading(true);
    setMessage('');

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/payments/initiate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          course: studentInfo.department,
          year: studentInfo.year,
          semester: studentInfo.semester,
          paymentType,
          amount
        })
      });

      const data = await response.json();

      if (response.ok) {
        setMessage('Payment initiated successfully! Please complete the payment process.');
        fetchPayments();
      } else {
        setMessage(data.message || 'Failed to initiate payment');
      }
    } catch (error) {
      setMessage('Error initiating payment: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Paid': return '#28a745';
      case 'Pending': return '#ffc107';
      case 'Failed': return '#dc3545';
      default: return '#6c757d';
    }
  };

  const structure = getStudentFeeStructure();
  const currentFeeAmount = calculateFeeAmount(structure, paymentType);

  return (
    <div className="menu-content">
      <h1>My Fees</h1>
      <p>View your fee structure and payment history.</p>

      {message && (
        <div style={{
          padding: '10px',
          margin: '10px 0',
          border: '1px solid #ddd',
          borderRadius: '4px',
          backgroundColor: message.includes('Error') || message.includes('Failed') ? '#ffebee' : '#e8f5e8',
          color: message.includes('Error') || message.includes('Failed') ? '#c62828' : '#2e7d32'
        }}>
          {message}
        </div>
      )}

      <div style={{ marginBottom: '20px' }}>
        <button
          onClick={() => setActiveTab('fees')}
          style={{
            padding: '10px 20px',
            marginRight: '10px',
            backgroundColor: activeTab === 'fees' ? '#007bff' : '#6c757d',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Fee Details
        </button>
        <button
          onClick={() => setActiveTab('payments')}
          style={{
            padding: '10px 20px',
            backgroundColor: activeTab === 'payments' ? '#007bff' : '#6c757d',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Payment History
        </button>
      </div>

      {activeTab === 'fees' && (
        <div>
          <h3>Fee Structure</h3>
          {structure ? (
            <div>
              <div style={{ marginBottom: '20px' }}>
                <h4>Course: {structure.course}</h4>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '15px', marginBottom: '20px' }}>
                  {Object.entries(structure.feeComponents).map(([key, value]) => (
                    <div key={key} style={{
                      border: '1px solid #ddd',
                      borderRadius: '8px',
                      padding: '15px',
                      backgroundColor: '#f8f9fa'
                    }}>
                      <h5>{key.charAt(0).toUpperCase() + key.slice(1).replace(/([A-Z])/g, ' $1')}</h5>
                      <p style={{ fontSize: '18px', fontWeight: 'bold', color: '#007bff' }}>
                        ₹{value}
                      </p>
                    </div>
                  ))}
                </div>

                <div style={{
                  border: '2px solid #007bff',
                  borderRadius: '8px',
                  padding: '20px',
                  backgroundColor: '#e7f3ff',
                  marginBottom: '20px'
                }}>
                  <h4>Total Fee Structure</h4>
                  <p style={{ fontSize: '24px', fontWeight: 'bold', color: '#007bff' }}>
                    Total Amount: ₹{structure.totalAmount}
                  </p>
                  <p>Semester Fee: ₹{structure.totalAmount / 2}</p>
                  <p>Year Fee: ₹{structure.totalAmount}</p>
                </div>
              </div>

              <div style={{ marginBottom: '20px' }}>
                <h4>Make Payment</h4>
                <div style={{ display: 'flex', gap: '15px', alignItems: 'center', marginBottom: '15px' }}>
                  <label>
                    <input
                      type="radio"
                      value="semester"
                      checked={paymentType === 'semester'}
                      onChange={(e) => setPaymentType(e.target.value)}
                    />
                    Pay for Semester (₹{structure.totalAmount / 2})
                  </label>
                  <label>
                    <input
                      type="radio"
                      value="year"
                      checked={paymentType === 'year'}
                      onChange={(e) => setPaymentType(e.target.value)}
                    />
                    Pay for Year (₹{structure.totalAmount})
                  </label>
                </div>

                <button
                  onClick={initiatePayment}
                  disabled={loading}
                  style={{
                    padding: '12px 24px',
                    backgroundColor: '#28a745',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: loading ? 'not-allowed' : 'pointer',
                    fontSize: '16px'
                  }}
                >
                  {loading ? 'Processing...' : `Pay ₹${currentFeeAmount} for ${paymentType === 'semester' ? 'Semester' : 'Year'}`}
                </button>
              </div>
            </div>
          ) : (
            <p>Loading fee structure...</p>
          )}
        </div>
      )}

      {activeTab === 'payments' && (
        <div>
          <h3>Payment History</h3>
          <div style={{ maxHeight: '600px', overflowY: 'auto' }}>
            {payments.length > 0 ? (
              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr style={{ backgroundColor: '#f8f9fa' }}>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Year</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Semester</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Type</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Amount</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Status</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {payments.map(payment => (
                    <tr key={payment._id}>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {payment.year}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {payment.semester}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {payment.paymentType}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        ₹{payment.amount}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        <span style={{
                          padding: '3px 8px',
                          borderRadius: '12px',
                          backgroundColor: getStatusColor(payment.status),
                          color: 'white',
                          fontSize: '12px'
                        }}>
                          {payment.status}
                        </span>
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {new Date(payment.createdAt).toLocaleDateString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>No payment records found.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default ViewFees;
