import React, { useState, useEffect } from 'react';
import './ManageReceipts.css';

const ManageReceipts = () => {
  const [receipts, setReceipts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [activeTab, setActiveTab] = useState('list');
  const [formData, setFormData] = useState({
    studentId: '',
    selectedPayment: null
  });
  const [studentDetails, setStudentDetails] = useState(null);
  const [studentPayments, setStudentPayments] = useState([]);
  const [fetchingData, setFetchingData] = useState(false);

  useEffect(() => {
    fetchReceipts();
  }, []);

  const fetchReceipts = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/receipts', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        setReceipts(data);
      }
    } catch (error) {
      console.error('Error fetching receipts:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // If studentId changed, fetch student details and payments
    if (name === 'studentId' && value.trim()) {
      fetchStudentData(value.trim());
    }
  };

  const fetchStudentData = async (studentId) => {
    // Only fetch if studentId is complete (assuming format STUXXXXXX)
    if (studentId.length < 9) {
      setStudentDetails(null);
      setStudentPayments([]);
      setFormData(prev => ({ ...prev, selectedPayment: null }));
      return;
    }

    setFetchingData(true);
    setStudentDetails(null);
    setStudentPayments([]);
    setFormData(prev => ({ ...prev, selectedPayment: null }));

    try {
      const token = localStorage.getItem('token');

      // Fetch student details
      const studentResponse = await fetch(`http://localhost:5000/api/payments/student-details/${studentId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (studentResponse.ok) {
        const studentData = await studentResponse.json();
        setStudentDetails(studentData);
      }

      // Fetch student payments
      const paymentsResponse = await fetch(`http://localhost:5000/api/payments/student/${studentId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (paymentsResponse.ok) {
        const paymentsData = await paymentsResponse.json();
        // Filter only paid payments
        const paidPayments = paymentsData.filter(payment => payment.status === 'Paid');
        setStudentPayments(paidPayments);
      }
    } catch (error) {
      console.error('Error fetching student data:', error);
      setMessage('Error fetching student data');
    } finally {
      setFetchingData(false);
    }
  };

  const handlePaymentSelect = (payment) => {
    setFormData(prev => ({
      ...prev,
      selectedPayment: payment,
      paymentId: payment.paymentId
    }));
  };

  const handleGenerateReceipt = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    if (!formData.selectedPayment) {
      setMessage('Please select a payment to generate receipt');
      setLoading(false);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/receipts/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ paymentId: formData.paymentId })
      });

      if (response.ok) {
        setMessage('Receipt generated successfully!');
        fetchReceipts();
        setFormData({
          studentId: '',
          selectedPayment: null
        });
        setStudentDetails(null);
        setStudentPayments([]);
        setActiveTab('list');
      } else {
        const error = await response.json();
        setMessage(error.message || 'Failed to generate receipt');
      }
    } catch (error) {
      setMessage('Error generating receipt: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadReceipt = async (receiptId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/receipts/${receiptId}/download`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `receipt_${receiptId}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      } else {
        setMessage('Failed to download receipt');
      }
    } catch (error) {
      setMessage('Error downloading receipt: ' + error.message);
    }
  };

  const handleCancelReceipt = async (receiptId) => {
    if (!window.confirm('Are you sure you want to cancel this receipt?')) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/receipts/${receiptId}/cancel`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        setMessage('Receipt cancelled successfully!');
        fetchReceipts();
      } else {
        setMessage('Failed to cancel receipt');
      }
    } catch (error) {
      setMessage('Error cancelling receipt: ' + error.message);
    }
  };

  return (
    <div className="manage-receipts-container">
      <div className="manage-receipts-header">
        <h1>Manage Receipts</h1>
        <p>Generate, view, and manage payment receipts.</p>
      </div>

      {message && (
        <div className={`message ${message.includes('Error') || message.includes('Failed') ? 'error' : ''}`}>
          {message}
        </div>
      )}

      <div className="tab-buttons">
        <button
          onClick={() => setActiveTab('list')}
          className={`tab-button ${activeTab === 'list' ? 'active' : ''}`}
        >
          View Receipts
        </button>
        <button
          onClick={() => setActiveTab('generate')}
          className={`tab-button ${activeTab === 'generate' ? 'active' : ''}`}
        >
          Generate Receipt
        </button>
      </div>

      {activeTab === 'generate' && (
        <div className="generate-section">
          <h3>Generate New Receipt</h3>
          <form onSubmit={handleGenerateReceipt}>
            <div className="form-group">
              <label>Student ID:</label>
              <input
                type="text"
                name="studentId"
                value={formData.studentId}
                onChange={handleInputChange}
                required
                placeholder="Enter student ID to fetch details"
              />
            </div>

            {fetchingData && (
              <div className="loading-text">
                Fetching student data...
              </div>
            )}

            {studentDetails && (
              <div className="student-details">
                <h4>Student Details:</h4>
                <p><strong>Name:</strong> {studentDetails.firstName} {studentDetails.middleName || ''} {studentDetails.lastName}</p>
                <p><strong>Course:</strong> {studentDetails.course}</p>
                <p><strong>Year:</strong> {studentDetails.year}</p>
              </div>
            )}

            {studentPayments.length > 0 && (
              <div className="payment-list">
                <label>Select Payment:</label>
                <div className="payment-scroll">
                  {studentPayments.map(payment => (
                    <div
                      key={payment._id}
                      onClick={() => handlePaymentSelect(payment)}
                      className={`payment-item ${formData.selectedPayment && formData.selectedPayment._id === payment._id ? 'selected' : ''}`}
                    >
                      <div><strong>Payment ID:</strong> {payment.paymentId}</div>
                      <div><strong>Amount:</strong> ₹{payment.amount}</div>
                      <div><strong>Date:</strong> {new Date(payment.paymentDate).toLocaleDateString()}</div>
                      <div><strong>Method:</strong> {payment.paymentMethod}</div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {studentPayments.length === 0 && formData.studentId && !fetchingData && (
              <div className="no-payments">
                No paid payments found for this student.
              </div>
            )}

            <button
              type="submit"
              disabled={loading || !formData.selectedPayment}
              className="submit-button"
            >
              {loading ? 'Generating...' : 'Generate Receipt'}
            </button>
          </form>
        </div>
      )}

      {activeTab === 'list' && (
        <div>
          <h3>Receipts List</h3>
          <div style={{ maxHeight: '600px', overflowY: 'auto' }}>
            {receipts.length > 0 ? (
              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr style={{ backgroundColor: '#f8f9fa' }}>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Receipt No</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Student ID</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Amount</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Status</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Date</th>
                    <th style={{ padding: '10px', border: '1px solid #ddd', textAlign: 'left' }}>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {receipts.map(receipt => (
                    <tr key={receipt._id}>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {receipt.receiptNumber}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {receipt.studentId}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        ₹{receipt.amount}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        <span style={{
                          padding: '3px 8px',
                          borderRadius: '12px',
                          backgroundColor: receipt.status === 'Active' ? '#28a745' : '#dc3545',
                          color: 'white',
                          fontSize: '12px'
                        }}>
                          {receipt.status}
                        </span>
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        {new Date(receipt.createdAt).toLocaleDateString()}
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #ddd' }}>
                        <button
                          onClick={() => handleDownloadReceipt(receipt._id)}
                          style={{
                            padding: '5px 10px',
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            marginRight: '5px'
                          }}
                        >
                          Download
                        </button>
                        {receipt.status === 'Active' && (
                          <button
                            onClick={() => handleCancelReceipt(receipt._id)}
                            style={{
                              padding: '5px 10px',
                              backgroundColor: '#dc3545',
                              color: 'white',
                              border: 'none',
                              borderRadius: '4px',
                              cursor: 'pointer'
                            }}
                          >
                            Cancel
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>No receipts found.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageReceipts;
