import React, { useState } from 'react';

const StaffViewDocuments = () => {
  // Mock data for demonstration. In a real app, this would come from the server.
  const [documents] = useState([
    {
      studentId: 'STU001',
      studentName: 'John Doe',
      documents: [
        { type: '10th (High School) Marksheet', status: 'Verified', fileName: '10th_marksheet.pdf' },
        { type: '10th (High School) Certificate', status: 'Verified', fileName: '10th_certificate.pdf' },
        { type: '12th (Intermediate) Marksheet', status: 'Pending', fileName: '12th_marksheet.pdf' },
        { type: '12th (Intermediate) Certificate', status: 'Verified', fileName: '12th_certificate.pdf' },
        { type: 'Transfer Certificate (TC) from last institute', status: 'Verified', fileName: 'tc.pdf' },
        { type: 'Character Certificate', status: 'Pending', fileName: 'character_certificate.pdf' },
        { type: 'Caste Certificate', status: 'Verified', fileName: 'caste_certificate.pdf' },
        { type: 'Domicile Certificate', status: 'Pending', fileName: 'domicile_certificate.pdf' },
        { type: 'Recent passport size photographs', status: 'Verified', fileName: 'passport_photo.jpg' },
        { type: 'Aadhaar Card or any valid Identity Proof', status: 'Verified', fileName: 'aadhaar.pdf' }
      ]
    },
    {
      studentId: 'STU002',
      studentName: 'Jane Smith',
      documents: [
        { type: '10th (High School) Marksheet', status: 'Verified', fileName: '10th_marksheet.pdf' },
        { type: '10th (High School) Certificate', status: 'Verified', fileName: '10th_certificate.pdf' },
        { type: '12th (Intermediate) Marksheet', status: 'Verified', fileName: '12th_marksheet.pdf' },
        { type: '12th (Intermediate) Certificate', status: 'Verified', fileName: '12th_certificate.pdf' },
        { type: 'Transfer Certificate (TC) from last institute', status: 'Verified', fileName: 'tc.pdf' },
        { type: 'Character Certificate', status: 'Verified', fileName: 'character_certificate.pdf' },
        { type: 'Caste Certificate', status: 'Verified', fileName: 'caste_certificate.pdf' },
        { type: 'Domicile Certificate', status: 'Verified', fileName: 'domicile_certificate.pdf' },
        { type: 'Recent passport size photographs', status: 'Verified', fileName: 'passport_photo.jpg' },
        { type: 'Aadhaar Card or any valid Identity Proof', status: 'Verified', fileName: 'aadhaar.pdf' }
      ]
    }
  ]);

  const handleDownload = (fileName) => {
    // In a real app, this would trigger a download from the server
    alert(`Downloading ${fileName}`);
  };

  return (
    <div className="view-documents">
      <h2>View All Student Documents</h2>
      {documents.map(student => (
        <div key={student.studentId} className="student-documents">
          <h3>{student.studentName} (ID: {student.studentId})</h3>
          <table>
            <thead>
              <tr>
                <th>Document Type</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {student.documents.map((doc, index) => (
                <tr key={index}>
                  <td>{doc.type}</td>
                  <td>{doc.status}</td>
                  <td>
                    <button onClick={() => handleDownload(doc.fileName)}>Download</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default StaffViewDocuments;
