import React, { useState } from 'react';

const UploadDocuments = () => {
  const [uploadedFiles, setUploadedFiles] = useState({});

  const documentTypes = [
    '10th (High School) Marksheet',
    '10th (High School) Certificate',
    '12th (Intermediate) Marksheet',
    '12th (Intermediate) Certificate',
    'Transfer Certificate (TC) from last institute',
    'Character Certificate',
    'Caste Certificate',
    'Domicile Certificate',
    'Recent passport size photographs',
    'Aadhaar Card or any valid Identity Proof'
  ];

  const handleFileChange = (event, docType) => {
    const file = event.target.files[0];
    if (file) {
      setUploadedFiles(prev => ({
        ...prev,
        [docType]: file.name
      }));
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Here you would typically send the files to the server
    alert('Documents uploaded successfully!');
  };

  return (
    <div className="upload-documents">
      <h2>Upload Documents</h2>
      <form onSubmit={handleSubmit}>
        {documentTypes.map(docType => (
          <div key={docType} className="document-upload">
            <label>{docType}:</label>
            <input
              type="file"
              accept=".pdf,.jpg,.jpeg,.png"
              onChange={(e) => handleFileChange(e, docType)}
            />
            {uploadedFiles[docType] && <span>Uploaded: {uploadedFiles[docType]}</span>}
          </div>
        ))}
        <button type="submit">Upload All Documents</button>
      </form>
    </div>
  );
};

export default UploadDocuments;
