          transform: translateY(-2px);
        }

        .status-card.paid {
          border-left: 4px solid #28a745;
        }

        .status-card.pending {
          border-left: 4px solid #ffc107;
        }

        .status-card.overdue {
          border-left: 4px solid #dc3545;
        }

        .status-icon {
          font-size: 32px;
          opacity: 0.8;
        }

        .status-content h3 {
          margin: 0 0 10px 0;
